import React, { useState } from "react";

type ItemType = {
  id: Number;
  title: String;
  children: Array<any>;
};

// Each "ItemType" element in the array have a array of children ID's. These refer
// to a ItemType.
// = This has a infinite depth.
const items: ItemType[] = [
  { id: 1, title: "Item 1", children: [2, 3, 4] },
  { id: 2, title: "Item 2", children: [1, 3, 4] },
  { id: 3, title: "Item 3", children: [1, 2, 4] },
  { id: 4, title: "Item 3", children: [1, 2, 3] }
];



const AddComment = () =>  {
  const arrayData = [
    { id: 1, 
      title: "Item 1",
      comments: "This is the comment", 
      children: [2, 3, 4] 
    },
    { id: 2, title: "Item 2", children: [1, 3, 4] },
    { id: 3, title: "Item 3", children: [1, 2, 4] },
    { id: 4, title: "Item 3", children: [1, 2, 3] }
  ];
  arrayData.push({ id: 5, title: "Item 2", comments: "This is the comment5", children: [1, 3, 4] });
  console.table(arrayData);

  return (event: React.MouseEvent)  =>  {
    event.preventDefault()
  }
}

function Item({ title, children }: ItemType): JSX.Element {
  const [open, toggle] = useState(false);

  // Given the children array with IDs, find each Item we refer to
  const childElements = children.map(findChild) as ItemType[];

  return (
    <div>
      <h6>
        {title} children <button onClick={() => toggle(!open)}>Open</button>
      </h6>
      <ul>
        {/* Loop children and run recursive */}
        {open &&
          childElements.map((child: ItemType) => (
            <li>
              <div>Child: {child.title}</div>
              <button onClick={AddComment()}>Reply</button>

              <Item {...child} />
            </li>
          ))}
      </ul>
    </div>
  );
}

function App() {
  return (

    <div>
      {/* Start by iterating over all items */}
      {items.map(item => (
        <Item {...item} />
      ))}
      <div className="comment-thread">
      <details open className="comment" id="comment-1">
        <a href="#comment-1" className="comment-border-link">
            <span className="sr-only">Jump to comment-1</span>
        </a>
        <summary>
            <div className="comment-heading">
                <div className="comment-voting">
                    <button type="button">
                        <span aria-hidden="true">&#9650;</span>
                        <span className="sr-only">Vote up</span>
                    </button>
                    <button type="button">
                        <span aria-hidden="true">&#9660;</span>
                        <span className="sr-only">Vote down</span>
                    </button>
                </div>
                <div className="comment-info">
                    <a href="#" className="comment-author">someguy14</a>
                    <p className="m-0">
                        22 points &bull; 4 days ago
                    </p>
                </div>
            </div>
        </summary>
        <div className="comment-body">
            <p>
                This is really great! I fully agree with what you wrote, and this is sure to help me out in the future. Thank you for posting this.
            </p>
            <button type="button">Reply</button>
            <button type="button">Flag</button>
        </div>
        <div className="replies">
          <details open className="comment" id="comment-2">
                <a href="#comment-2" className="comment-border-link">
                    <span className="sr-only">Jump to comment-2</span>
                </a>
          </details>
          <summary>
              <div className="comment-heading">
                  <div className="comment-voting">
                      <button type="button">
                          <span aria-hidden="true">&#9650;</span>
                          <span className="sr-only">Vote up</span>
                      </button>
                      <button type="button">
                          <span aria-hidden="true">&#9660;</span>
                          <span className="sr-only">Vote down</span>
                      </button>
                  </div>
                  <div className="comment-info">
                      <a href="#" className="comment-author">randomperson81</a>
                      <p className="m-0">
                          4 points &bull; 3 days ago
                      </p>
                  </div>
              </div>
          </summary>

          <div className="comment-body">
              <p>
                  Took the words right out of my mouth!
              </p>
              <button type="button">Reply</button>
              <button type="button">Flag</button>
          </div>
        </div>


        </details>
      </div>
    </div>
  );
}

// Function to find a Item by child id.
function findChild(childId: Number): ItemType | undefined {
  return items.find(({ id }) => id === childId);
}

export default App;
