import React from "react"
import { commentData, FileNode } from "./constants/data"

const replyComment = () => {
    console.log("AAAAA"+this)

    return (event : React.MouseEvent) =>  (

    event.preventDefault()
  )
}


function ActionLink() {
  function handleClick(e: any) {
    e.preventDefault();
    console.log('The link was clicked.');
  }
  return (
    <a href="#" onClick={handleClick}>
      Click me
    </a>
  );
}

function Comments({comment} : {comment: FileNode})   {
  const nestedComments = (comment.children || []).map(comment => {
    //comment.id
    return ( <Comments comment={comment} /> )
  })

  //console.table(comment.children);  
  //<div style={{"marginLeft": "25px", "marginTop": "10px"}}>
  //<div>{comment.id} and {comment.author} and {comment.text}</div>
  return (
    <div className="comment-thread">  
      <details open className="comment" id="comment-1">
        <a href="#comment-1" className="comment-border-link">
              <span className="sr-only">Jump to comment-1</span>
          </a>
          <summary>
            <div className="comment-heading">
                <div className="comment-voting">
                    <button type="button">
                        <span aria-hidden="true">&#9650;</span>
                        <span className="sr-only">Vote up</span>
                    </button>
                    <button type="button">
                        <span aria-hidden="true">&#9660;</span>
                        <span className="sr-only">Vote down</span>
                    </button>
                </div>
                <div className="comment-info">
                    <a href="#" className="comment-author">{comment.author}</a>
                    <p className="m-0">
                      {comment.id} &bull; days ago
                    </p>
                </div>
            </div>
        </summary>
        <div className="comment-body">
            <p>{comment.text}</p>
            <button onClick={replyComment}>Reply</button>
            <button value="blue" onClick={e => changeColor(e.target.addEventListener)}>Color Change</button>

        </div>
        {nestedComments}
      </details>

    </div>
  )
}



function App() {
  return (
    <div>
      <Comments comment={commentData}  />
    </div>
  )
}

export default App

function changeColor(value: any): void {
  console.log(value);

}
